mapper.update:
update cn_note
set cn_notebook_id=#{bookId}
where cn_note_id=#{noteId}

update cn_note
set cn_note_status_id='2'
where cn_note_id=#{id}

UPDATE cn_note
SET cn_note_title=#{cn_note_title},
	 cn_note_body=#{cn_note_body},
	 cn_note_last_modify_time=#{cn_note_last_modify_time}
